<table id="module_list" class="table_list">	<tr>		<th colspan="5"><?php echo Functions::__Translate("Module permission list") ?></th>	</tr>	<tr>		<th><?php echo Functions::__Translate("Module") ?></th>		<th><?php echo Functions::__Translate("Permission") ?></th>		<th><?php echo Functions::__Translate("Description") ?></th>		<th><?php echo Functions::__Translate("Status") ?></th>		<th><?php echo Functions::__Translate("Actions") ?></th>	</tr><?php if($__rows) 		  { 		  	foreach ($__rows AS $__row)			{ 				$Row = (object)$__row;				$class_row = ($class_row == "" || $class_row == "odd_row") ? "even_row" : "odd_row" ;				$inactive_item = ($Row->active == "0") ? "inactive_item" : "";				?>					<tr class="<?php echo $class_row . " " . $inactive_item ?>" id="container-cat_module-<?php echo $Row->cat_module_id ?>">						<td class="left">							<a href="<?php echo TO_ROOT ?>/apps/admin/module.php?cat_module_id=<?php echo $Row->cat_module_id ?>">							<?php echo ($Row->module_context) ? Functions::__Translate($Row->module_context) : Functions::__Translate($Row->module) ?> 							</a> 						</td>												<td class="left">							<a href="<?php echo TO_ROOT ?>/apps/admin/module_permission.php?cat_module_permission_id=<?php echo $Row->cat_module_permission_id ?>">							<?php echo ($Row->permission_context) ? Functions::__Translate($Row->permission_context) : Functions::__Translate($Row->permission) ?>							</a>						</td>												<td class="left"><?php echo $Row->description ?></td>												<td class="center"><?php echo ($Row->active == "0") ? Functions::__Translate("Inactive") : Functions::__Translate("Active") ?></td>												<td class="active_actions">							<a href="<?php echo TO_ROOT ?>/apps/admin/edit_module_permission.php?cat_module_permission_id=<?php echo $Row->cat_module_permission_id ?>"><?php echo Functions::__Translate("Edit") ?></a>							<a class="active_action" id="cat_module-active-<?php echo $Row->cat_module_id ?>-<?php echo $Row->active ?>"><?php echo ($Row->active == "1") ? Functions::__Translate("Inactive") : Functions::__Translate("Active") ?></a>						</td>					</tr>		<?php } //end principal foreach $__rows ?>	<?php } //end principal if ?></table>