<table id="user_list" class="table_list">	<tr class="table_header">		<th colspan="<?php echo count($cat_user_roles) + 1 ?>"><?php echo Functions::__Translate("Roles for users") ?></th>	</tr>	<?php if($cat_user_roles){ ?>		<tr class="table_titles">			<th><?php echo Functions::__Translate("User name") ?></th>			<?php foreach($cat_user_roles AS $rol_id => $rol){ ?>					<th><?php echo Functions::__Translate(ucfirst($rol)) ?></th>			<?php } ?>		</tr>	<?php } ?><?php if($cat_users) 		  { 		  	foreach ($cat_users AS $user)			{ 				$user_login_id = $user["user_login_id"];				$User = Login::getInstance($user_login_id);				$User->load();				$User->loadUserData();				$User->loadRolesData();								$class_row = ($class_row == "" || $class_row == "odd_row") ? "even_row" : "odd_row" ;				?>					<tr class="<?php echo $class_row . " " . $inactive_item ?>" id="container-cat_user_x_rol-<?php echo $user_login_id ?>">						<td class="right"><a href="<?php echo TO_ROOT ?>/sections/profile.php?user_login_id=<?php echo $user_login_id ?>">							<?php echo $User->data["names"] . " " . $User->data["lastname"] . " " . $User->data["mother_name"] ?> 							</a> 						</td>						<?php foreach($cat_user_roles AS $rol_id => $rol)							  { 							  	$active = ($User->data["roles"][$rol_id]["active"]) ? $User->data["roles"][$rol_id]["active"] : 0 ; 							  	$cat_user_x_rol_id = ($User->data["roles"][$rol_id]["cat_user_x_rol_id"]) ? $User->data["roles"][$rol_id]["cat_user_x_rol_id"] : 0 ; ?>								<td>									<input type="checkbox" class="set_rol" <?php if($active == "1" ) { ?> checked="checked" <?php } ?> id="<?php echo "cat_user_x_rol-" . $cat_user_x_rol_id . "-" . $rol_id . "-" . $User->data["user_id"] . "-" . $active  ?>" />								</td>						<?php } ?>					</tr>		<?php } //end principal foreach $__rows ?>	<?php } //end principal if ?></table>