<?php define("TO_ROOT", "../..");   require_once TO_ROOT . "/core/includes/main.inc.php";   $Page = new Page("User permission", "user_permission");    /* ---------------- <<<< Assert User Permissions >>>> ---------------------------------------------------------------------*/  /** *  if(!System::__assertLoginType($_SESSION["login_type"]) && !System::__hasPermission("user", "view_permission"))  {  	System::__sessionDestroy();  	$Page->setOnReady("__showMessage","{'message':'Sorry you dont have permission','options':{'OK':function(){__goToPage('index.php')}}}");  	$Page->goToPage();  }  /* ---------------- <<<< End Assert User Permissions >>>> ----------------------------------------------------------------*/      /* ---------------- <<<< Load Class Models >>>> --------------------------------------------------------------------------*/  $user_login_id = (isset($_GET["user_login_id"])) ? $_GET["user_login_id"] : false ;  if(!$user_login_id)  {  	//$Page->setOnReady("__showMessage","{'message':'Sorry this user dont exist','options':{'OK':function(){__goToPage('user_list.php')}}}");  }    $Login = Login::getInstance($user_login_id);  $Login->load();  $Login->loadUserData();  $Login->loadPermissionData();    $modules = Functions::__getCatModule();  /* ---------------- <<<< End Load Class Models >>>> ---------------------------------------------------------------------*/      /* ---------------- <<<< Set Genarl Variables >>>> ----------------------------------------------------------------------*/  $Page->assign("Login", $Login);  $Page->assign("modules", $modules);  $Page->assign("user_modules", $Login->getUserModules());  /* ---------------- <<<< End Set Genarl Variables  >>>> -----------------------------------------------------------------*/      /* ---------------- <<<< Configure Js Actions >>>> ----------------------------------------------------------------------*/    /* ---------------- <<<< End Configure Js Actions >>>> -----------------------------------------------------------------*/      /* ---------------- <<<< Set Menus >>>> --------------------------------------------------------------------------------*/  $Page->setSecondaryMenu("admin_users");  /* ---------------- <<<< End Set Menus >>>> ----------------------------------------------------------------------------*/      /* ---------------- <<<< Configure CSS Theme >>>> ----------------------------------------------------------------------*/  $Page->addCssLink(TO_ROOT . "/apps/admin/subcore/style/permission.css");  /* ---------------- <<<< End Configure CSS Theme >>>> ------------------------------------------------------------------*/      /* ---------------- <<<< Configure JS Scripts >>>> ---------------------------------------------------------------------*/  $Page->addJsLink(TO_ROOT . "/apps/admin/subcore/scripts/script.user_permission.js");  /* ---------------- <<<< End Configure JS Scripts >>>> -----------------------------------------------------------------*/      $Page->display();  